var sortByRegion=function(arr)
{var asia={"continent":"Asia","countries":[]}
var europe={"continent":"Europe","countries":[]}
var africa={"continent":"Africa","countries":[]}
var americas={"continent":"Americas","countries":[]}
var polar={"continent":"Polar","countries":[]}
var oceania={"continent":"Oceania","countries":[]}
for(var i=0;i<arr.length;i++)
{switch(arr[i].region)
{case "Asia":asia.countries.push(arr[i].name);break;case "Europe":europe.countries.push(arr[i].name);break;case "Africa":africa.countries.push(arr[i].name);break;case "Americas":americas.countries.push(arr[i].name);break;case "Polar":polar.countries.push(arr[i].name);break;case "Oceania":oceania.countries.push(arr[i].name);break}}
asia.countries.sort();europe.countries.sort();africa.countries.sort();americas.countries.sort();polar.countries.sort();oceania.countries.sort();var res=[];res.push(asia);res.push(europe);res.push(africa);res.push(americas);res.push(polar);res.push(oceania);return res}
$.ajax({method:'GET',url:'https://restcountries.eu/rest/v1/all'}).done(function(data){console.log(data);var countries=sortByRegion(data);console.log(countries);var listRegion;countries.forEach(function(region){listRegion+='<optgroup label="'+region.continent+'">';region.countries.forEach(function(country){listRegion+='<option value="'+country+'">'+country+'</option>'});listRegion+='</optgroup>'});$('#country').append(listRegion)})