$('.swipe-footer').hide()
$('.swipe-helper').find('img').hide().css('opacity','1').fadeIn()
$('.active').find('.swipe-footer').fadeIn()
$('.swipe-item').click(function(){$('.swipe-section').children().removeClass('active')
$('.swipe-footer').hide()
$(this).addClass('active')
$(this).find('.swipe-footer').fadeIn()})
var swipes=[]
$('.swiper').each(function(){swipes.push(new Swiper(this))})
var productsSlider=new Hammer(document.querySelector('.swipe-section'),null);productsSlider.on('swipe',function(e){if(e.offsetDirection==2){swipes[0].swipeToNext()}
if(e.offsetDirection==4){swipes[0].swipeToPrev()}})